import './App.css';
import { useState } from 'react';

function App() {
  let [posts, postChange] = useState([
    {
      id: 1,
      img: './img/japan_01.jpeg',
      desc: 'Í∞ÑÏÇ¨Ïù¥ Ïó¨Ìñâ Ï≤´Î≤àÏß∏ Ïû•ÏÜåÎäî Ïò§ÏÇ¨Ïπ¥ÏÑ± üèØ'
    },
    {
      id: 2,
      img: './img/japan_02.jpeg',
      desc: 'Í∞ÑÏÇ¨Ïù¥ Ïó¨Ìñâ ÎëêÎ≤àÏß∏ Ïû•ÏÜåÎäî ÎÇòÎùº ÏÇ¨Ïä¥Í≥µÏõê ü¶å'
    },
    {
      id: 3,
      img: './img/japan_03.jpeg',
      desc: 'Í∞ÑÏÇ¨Ïù¥ Ïó¨Ìñâ ÏÑ∏Î≤àÏß∏ Ïû•ÏÜåÎäî ÍµêÌÜ† Í∏∞ÏöîÎØ∏Ï¶àÎç∞Îùº ‚õ© Îã®ÌíçÏù¥ Ï†àÍ≤ΩÏù¥ÏòÄÎã§!'
    },
    {
      id: 4,
      img: './img/japan_04.jpeg',
      desc: 'Í∞ÑÏÇ¨Ïù¥ Ïó¨Ìñâ 4Î≤àÏß∏Îäî ÏáºÌïëÎç∞Ïù¥~üí∏'
    },
    {
      id: 5,
      img: './img/japan_05.jpeg',
      desc: 'Í∞ÑÏÇ¨Ïù¥ Ïó¨Ìñâ Îã§ÏÑØÎ≤àÏß∏ Ïû•ÏÜåÎäî Ïú†ÎãàÎ≤ÑÏÖú Ïä§ÌäúÎîîÏò§ üé°'
    },
    {
      id: 6,
      img: './img/japan_06.jpeg',
      desc: 'Í∞ÑÏÇ¨Ïù¥Ïó¨Ìñâ ÎßàÏßÄÎßâ Ïó¨ÌñâÏßÄÎäî ÍµêÌÜ† ÏïÑÎùºÏãúÏïºÎßà üç° ÏÇ¨Ïã§ Ïó¨Í∏∞Í∞Ä Ï†úÏùº Ï¢ãÏïÑÌïòÎäî Ïû•ÏÜå„Öé„Öé'
    },
  ]);

  return (
    <div className='App'>
      <header className='header'>
        <h1 className='title'>My Japan Travel</h1>
      </header>
      <section className='content'>
        <inputForm></inputForm>
        {
          posts.map((a,i) => {
            return <Post posts={a} num={i} key={i}/>
          })
        }
      </section>
      <footer className='footer'>
        <h3 className='copyright'>Copyright kangDayeon</h3>
      </footer>
    </div>
  );
}
const inputForm = () => {
  return(
    <form className='inputBox'>
      <input className="text"></input>
    </form>
  )
}

const Post = (props) => {

  return(
    <div className='post'>
      <h3 className='post_title'>Day {props.posts.id}</h3>
      <div className='post_content'>
        <div className='post_img_wrap'>
          <img className='post_img' src={props.posts.img}></img>
        </div>
        <p className='post_desc'>{props.posts.desc}</p>
      </div>
    </div>
  )
}

export default App;
